#include "pch.h"
#include "Student.h"
#include<string>
using namespace std;

void Student::setStudentId(int studentId) {
	this->studentId = studentId;

}
int Student::getStudentId() {
	return studentId;
}
void Student::setProgram(string program) {
	this->program = program;

}
string Student::getProgram() {
	return program;
}
void Student::setCoop(int coop) {

	this->coop = coop;
}
int Student::getCoop() {

	return coop;
}
ostream& operator << (ostream& cout, Student& s) {
	cout << "Student's name: " << s.getName() << endl;
	cout << "Student's age: " << s.getAge() << endl;
	cout << "Student's Address: " << s.getAddress() << endl;
	cout << "Student's Id: " << s.getStudentId() << endl;
	cout << "Student's program: " << s.getProgram() << endl;
	cout << "Student's Coop: " << s.getCoop() << endl;
	return cout;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                

}
Student::Student()
{
	studentId = 0;
	program = "no program";
	coop = 0;
}
Student::Student(int studentId,string program,int coop):studentId{studentId},program{program},coop{coop}{}

